#include <bits/stdc++.h>
using namespace std;

class Solution
{
public:

    int longestPalindrome(string s)
    {
        unordered_map<char, int> m;

        for (int i = 0; i < s.length(); i++)
        {
            m[s[i]]++;
        }
        int result = 0;
        bool is_odd = false;

        for (auto pr : m)
        {
            if (pr.second % 2 == 0)
            {
                result = result + pr.second;
            }
            else
            {
                is_odd = true;
                result = result+pr.second -1;
            }
        }

        if(is_odd) result++;

        return result;
    }
};

int main()
{

    Solution s1;

    int ans = s1.longestPalindrome("");

    cout << "Ans: " << ans << endl;

    return EXIT_SUCCESS;
}

